                    <tbody>
                      {results.subjects.map((subject, index) => (
                        <tr key={index}>
                          <td>{subject.name}</td>
                          <td>{subject.total}</td>
                          <td>{subject.obtained}</td>
                          <td>{subject.percentage}%</td>
                          <td>
                            <span className={`grade-badge ${getGradeColor(subject.grade)}`}>
                              {subject.grade}
                            </span>
                          </td>
                          <td>{subject.remarks}</td>
