                        <th>Total</th>
                        <th>Obtained</th>
                        <th>%</th>
                        <th>Grade</th>
                      </tr>
                    </thead>
                    <tbody>
                      {studentResult.subjects.map((subject, index) => (
                        <tr key={index}>
                          <td>{subject.name}</td>
                          <td>{subject.total}</td>
                          <td>{subject.obtained}</td>
                          <td>{subject.percentage}%</td>
                          <td>
                            <span className={`grade-badge ${getGradeColor(subject.grade)}`}>
                              {subject.grade}
                            </span>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
